// Disclaimer: I'm just experimenting something I didn't know 10 minutes ago.
// don't trust anything ! ( correctness & discretization ). :-)
// For a start, proper normalization is required, but it needs the gamma() function.
// Then the integration near the poles should surely be thinner.

#define ch iChannel0
#define T(u) texelFetch(ch, ivec2(u)%textureSize(ch,0), 0 )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, D = vec2(1,0),
         U = ( 2.*u - R ) / R.y;

    float t = 0., m = 0., w, p = 2.*iMouse.y/R.y;
    int N,k;
    O-=O;

    if (U.x > 1.) {                         // --- Laplacian
        O = T(u+D)+T(u-D)+T(u+D.yx)+T(u-D.yx) - 4.*T(u);
        t=1.; m = .2;
    }
    else if (U.x > 0.) {                    // --- Fractional Laplacian
        for ( N=21,k=0; k < N*N; k++ ) {
            D = vec2(k%N,k/N) -float(N-1)/2.;
            if (D==vec2(0)) continue;
            t += w = 1./ pow( length(D), 2. + 2.*p );
            O += w* ( T(u)-T(u+D) );
            m = max(m, abs( (T(u)-T(u+D)).x ) );
          }
        m = .07;
    }
    else if (U.x < -1.) {                    // --- Gradient
        O = vec4( T(u+D).x - T(u).x, T(u+D.yx).x - T(u).x, 0,0 );
        t=1.; m = .07;
    }
    else {                                  // --- Fractional Gradient
        for ( N=51,k=1; k < N; k++ ) {   // N should be larger, but lack of normalization 
            D = vec2(-k,0);
            if (D==vec2(0)) continue;
            t += w = 1./ pow( length(D), p );
            O += w* vec4( T(u+D).x, T(u+D.yx).x, 0,0 );
            m = max(m, abs( (T(u+D)).x ) );
        }
        O.x = dFdx(O.x);  O.y = dFdy(O.y);
        m = .07;
     // O = vec4( dot(O,vec4(.7,.7,0,0)) );
    }

    O = .5 + O/t/m;
}
