vec3 white(float d,int n,float d0)
{
    float x = 0.5/float(n);
    vec3 c1 = vec3(0.0);
    vec3 c2 = vec3(0.0);
    c1 = vec3(smoothstep(d0,d0-0.01,d));
    if(d0-x > 0.001)
    {
        c2 = vec3(smoothstep(d0-x,d0-x+0.01,d));
    }
    else
        c2 = vec3(1.0,1.0,1.0);

    return c1*c2;
}
vec3 rings(vec2 uv,int n)
{
    vec3 res = vec3(0.0);
    float d = length(uv);
    float x = 0.5/float(n);
    for(int i=0;i<n;i+=2)
    {
        res += white(d,n,0.5-float(i)*x);
    }
    return res;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec2 uv = (fragCoord-mouse*iResolution.xy)/iResolution.y;//将原点移动到鼠标点击位置
    vec3 color = vec3(0.0);

    color = rings(uv,5);

    fragColor = vec4(color,1.0);
}
