float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    uv.x /= cosh(1.5 * uv.x);

    float a = atan(uv.y, uv.x);
    float r = uv.x * thc(4., 1. * length(uv) + 0.5 * iTime) + uv.y * ths(4.,0.5 * iTime);

    float time = 40. * r + 1. * a + 2. * iTime;
    float str = 4.;// * cos(uv.x * 20.);
    float v = 100.;

    float sc = 0.75 + 0.25 * thc(4., .25 * iTime);
    vec2 p = 0.3 * vec2(thc(cos(v * uv.x + time) * str, sc * v * uv.x + time), 
                        ths(sin(v * uv.y + time) * str, sc * v * uv.y + time));

    float d = length(uv-p);
    float s = smoothstep(-0.01,0.01,0.1 - d);
    vec3 col = vec3(s);

    fragColor = vec4(col,1.0);
}
