void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2( fragCoord.xy / iResolution.xy );

    vec4 col1=vec4(0.5,0.8,0.9,1.0);
    vec4 col2 = vec4(0.4,0.75,0.9,1);

    float x = (iTime/2.0)+uv.x;
    float line1 = ((0.3)*sin(5.0*x)+(0.5)*sin(2.0*x)+0.2*sin(0.2*x)+1.0);//4.0;
    float line2 = 0.3*sin(0.2*x)+(0.2)*sin(7.0*x)+0.3*sin(-7.0*x)+1.0;
    float line3 = 0.2*cos(4.0*x)+(0.5)*cos(0.3*x)+0.2*cos(9.0*x)+1.0;
    float line4 = (0.33*cos(5.0*x)+(0.75)*cos(2.0*x)+0.8*cos(0.2*x)+1.0);
    //float line3 = 0.2*cos(4.0*x)+(0.5)*cos(0.3*x)+0.2*cos(9.0*x)+1.0;

    vec4 c = col2;
    if (uv.y < line1 && uv.y > line1-0.02) c = col1;
    if (uv.y < line2 && uv.y > line2-0.02) c = col1;
    if (uv.y < line3 && uv.y > line3-0.01) c = col1;
    if (uv.y < line4 && uv.y > line4-0.01) c = col1;

    fragColor=c;

}
