void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*200.-100.;
    uv.x *= iResolution.x/iResolution.y;
    vec2 ms = iMouse.xy/iResolution.xy*2.-1.;
    float grd = 0.09;

    vec3 col = vec3(0);

    vec2 st = uv;



    const int MAX = 3;
    float final = 0.;
    for(int x=-MAX;x<MAX;x++)
    {
        for(int y=-MAX;y<MAX;y++)
        {
            float _x = float(x);
            float _y = float(y);
            _x += sin(iTime+_x*_y);
            _y += cos(iTime+_y*_x);
            vec2 xy = vec2(_x,_y) * (1.-length(vec2(_x,_y)+ms*10.));
            float dis = distance(st,xy);
            final += 1./dis;
        }
    }

    float v = 1./final*float(MAX);
    v = clamp(v,0.,1.);
    v = floor(v);
    col += 1.-v;

    col = pow(col,vec3(.4545));

    fragColor = vec4(col,1.0);
}
