float weirdness = 0.1;
float xs = 720.0;
float ys = 360.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Grab a pixel from the noise image
    vec4 noise = texture(iChannel1, vec2(iTime / 30.0, uv.y));

    vec3 a = texture(iChannel2, vec2(uv.x, uv.y)).xyz;
    float add = a.x + a.y + a.z;
    add /= 3.0;

    float s = abs(sin(uv.y * ys));
    float addon = s > 0.9 ? -1.0 : 0.0;
    float t = abs(cos(uv.x * xs));
    addon += t > 0.9 ? -0.03 : 0.0;

    // Get a sin vertical function
    float shift = (clamp(sin((iTime / 20.0)), 0.5, 1.0) * (((noise.x + noise.y + noise.z) / weirdness)) * 360.0 * (weirdness > 0.0 ? 1.0 : 0.0));
    float xshift = (shift > 0.0 ? sin(uv.y * 360.0) / shift : 0.0);

    noise = xshift > 0.0 ? noise : vec4(0.0, 0.0, 0.0, 0.0);

    // Separate r, g, b a bit
    vec2 uuv = uv + vec2(0.01 + xshift + noise.x / 100.0 + add + iTime / 8.0, 0.01);
    float xv = floor(uuv.x * xs) / xs;
    float yv = floor(uuv.y * ys) / ys;
    vec4 scol = texture(iChannel0, vec2(xv, yv));
    float b = texture(iChannel0, vec2(xv, yv)).z;

    // Output to screen
    fragColor = vec4(scol.x/* + addon*/, scol.y/* + addon*/, scol.z/* + addon*/, b);
}
