void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord/iResolution.xy);
}

float rand(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }
float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }

const ivec2 offsets[] = ivec2[] (
    ivec2(0,   0),
    ivec2(0,   0),
    ivec2(0,   1),
    ivec2(1,   1),
    ivec2(1,   0),
    ivec2(1,  -1),
    ivec2(0,  -1),
    ivec2(-1, -1),
    ivec2(-1,  0),
    ivec2(-1,  1)
);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if(iTime <= 0.1) {
        fragColor = texture(iChannel0, fragCoord/iResolution.xy);
    } else {

        if(rand(iTime)*rand(fragCoord) > 0.01){
            int offset = int(round((rand(fragCoord*iMouse.xy))*10.0));
            fragColor = texelFetch(iChannel1, ivec2(fragCoord) - offsets[offset], 0);
        }else{

        fragColor = texelFetch(iChannel1, ivec2(fragCoord), 0);    
        }
    }
}
