vec2 mul(vec2 p,vec2 c)
{
    return vec2(p.x*p.x-p.y*p.y,2.0*p.x*p.y)+c;
}
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec2 myMouse=iMouse.xy/iResolution.xy;
    float zoom=pow(60.,-myMouse.x*3.);
    vec2 newZ=vec2(0.0);
    float maxStep=1000.;
    float Zstep=0.0;
    vec2 c=(uv)*zoom*3.+vec2(0.2537269133080432,0.000365995381749671135);
    for(float i=0.0;i<maxStep;i+=1.0)
    {
        if(length(newZ)>2.0)break;
        newZ=mul(newZ,c);
        Zstep+=1.0;
    }

    // Output to screen
    float num=Zstep/maxStep;
    vec3 col=vec3(num);
    fragColor = vec4(col,1.0);
}
