vec3 NumberPlane(vec2 uv) {
    vec3 col = vec3(0.);

    // Draw Grid
    if (
        fract(uv).x <= fwidth(uv.x) ||
        fract(uv).y <= fwidth(uv.y)
    ) {
        col = vec3(1.);
    }

    // Draw x=0
    if (abs(uv.x) <= fwidth(uv.x)) {
        col = vec3(.0, 1., .0);

    // Draw y=0
    } else if (abs(uv.y) <= fwidth(uv.y)) {
        col = vec3(1., .0, .0);
    }

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = 3. * (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    fragColor = vec4(NumberPlane(uv), 1.);
}
