#define pi 3.14159

#define Blur .0075
#define Zoom 6.
#define Thickness .05

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    //"Variables are slow" they said.
    fragColor = vec4(vec3(smoothstep(Blur, 0., abs(length(fract(uv * Zoom) - .5) - .5) - (cos(length(uv - (iMouse.xy - iResolution.xy * .5) / iResolution.y) * (Zoom / .3) * pi + iTime * 10.) * Thickness + Thickness))), 1.);
}
