bool dentroCuadrado(vec2 centro, float ancho, vec2 fragCoord)
{
    return abs(fragCoord.x - centro.x) < ancho / 2.0 &&
        abs(fragCoord.y - centro.y) < ancho / 2.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float modx = mod(fragCoord.x - iTime * 50.0, 100.0);
    float mody = mod(fragCoord.y - iTime * 50.0, 100.0);

    bool dentro = dentroCuadrado(vec2( 0.0, 0.0), 90.0, vec2(modx, mody));


    if (dentro)
        fragColor = vec4(vec3(1.0, 0.0, 0.0), 1.0);
    else
        fragColor = vec4(vec3(0.0, 0.0, 1.0), 1.0);

}
