void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float red   =  cos(fragCoord.x * fragCoord.y + iTime * 7.12);
    float green =  sin(fragCoord.x * fragCoord.y + iTime * 0.53);
    float blue  = -sin(fragCoord.x * fragCoord.y + iTime * 2.0);

    // Output to screen
    fragColor = vec4(red, green, blue, 1.0);
}
