#define radial true
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Create UVs    
    vec2 uv;
    if (radial){
        uv = vec2(length((fragCoord-.5*iResolution.xy)/iResolution.y));
    }else{
        uv = vec2(fragCoord.x/iResolution.x);
    }    

    // Time varying pixel color
    vec3 baseCol = mix(vec3(0,0,1),vec3(0,.5,.5),uv.x);
    float val = 0.5 * (texture(iChannel0, uv).x + 0.5) * (1.-uv.x*uv.x);
    vec3 col = (mix(baseCol,vec3(1,0,0),val) + .1+.1*cos(iTime+uv.yxy+vec3(0,2,4)))*val;

    // Output to screen
    fragColor = vec4(col,1.0);
}
