#define inv_root3 1./(1.7320508/2.)

vec2 skew(vec2 st)
{
    vec2 skew_st;
    skew_st.x = st.x*inv_root3;
    skew_st.y = st.y + 0.5*st.x;
    return skew_st;
}

vec3 simplexGrid(vec2 st)
{
    vec3 dst;
    st = skew(st);
    st = fract(st);
    if(st.y>st.x){
        dst.x = st.x;
        dst.y = st.y-st.x;
        dst.z = 1.-st.y;
    }else{
        dst.x = 1.-st.x;
        dst.y = st.x-st.y;
        dst.z = st.y;
    }
    return dst;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st *=4.;

    vec3 col = simplexGrid(st);
    fragColor = vec4(col, 1.);
}
