// Controls in Common

void mainImage( out vec4 Q, in vec2 U )
{
    vec4 
        n = (C(U+vec2(0,1))),
        e = (C(U+vec2(1,0))),
        s = (C(U-vec2(0,1))),
        w = (C(U-vec2(1,0)));
    vec4 c = A(U).xyzw;
    vec3 no = normalize(vec3(e.x-w.x+e.y-w.y,n.x-s.x+n.y-s.y,.5));
    Q = vec4(1,.7,.4,1)-vec4(1,.5,0,0)*c.wwww;
    float a = .5;
    no.zy *= mat2(cos(a),-sin(a),sin(a),cos(a));
    no.zx *= mat2(cos(a),-sin(a),sin(a),cos(a));
    Q*=max(0.,.2+.8*no.z);


}

// Forces ðŸ¦œ
Main
{
    Q = A(U);
    vec4 c = C(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y)) 
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        vec4 c = C(U+u);
        float f = 0.25*(a.w+c.x);
        dQ.xy -= f*u;
    }
    Q += dQ;
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
    // erosion 
    c.z;
    // sedimentation
    c.w;
    Q.w += 1e-5;
    if (Q.w>0.) Q.z = (Q.w*Q.z+c.z-c.w)/Q.w;
    if (U.x<1.&&abs(U.y-.5*R.y)<20.) Q.w *= 0.;
    vec2 M = 1.5*R;
    if (iMouse.z>0.) M = iMouse.xy;
    Q = clamp(Q,-3.,3.);
    if(length(U-M)<.02*R.y)Q.w = 1.;
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
    if (iFrame < 1) Q = vec4(0,0,0,.3);

}

// Advect ðŸ¿
Main
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       float wa = 0.5*Q.w*min(i,.51);
       float wb = 0.5*q.w*max(i-.51,0.);
       dQ.xyz += Q.xyz*wa+q.xyz*wb;
       dQ.w += wa+wb;
    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
}

Main {
    Q = C(U);
    vec4 a = A(U);
    vec4 
        n = (C(U+vec2(0,1))),
        e = (C(U+vec2(1,0))),
        s = (C(U-vec2(0,1))),
        w = (C(U-vec2(1,0)));
    Q.x -= .0005*(Q.x-0.25*(n+e+s+w).x);
    // erosion
    Q.z = 1e-2*length(a.xy)*a.w;
    // sedimentation
    Q.w = 1e-2*max(1.-length(a.xy),0.)*a.w*a.z;

    Q.x -= Q.z-Q.w;
    Q.x = clamp(Q.x,-2.,2.);
    if (iFrame < 100)
        Q = vec4(B(U).x,0,0,0);
}
