vec2 rotate(vec2 uv, float th) {
  return mat2(cos(th), cos(th)*sin(th), -sin(th), cos(th)) * uv;
}

float sdfSquare(vec2 uv, float size, vec2 offset) {
  float x = uv.x - offset.x;
  float y = uv.y - offset.y;
  vec2 rotated = rotate(vec2(x,y), iTime);
  float d = max(abs(rotated.x), abs(rotated.y)) - size;

  return d > 0. ? 0.0 : 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy; // <0, 1

  vec2 originalUV = uv;
  uv -= 0.5; // <-0.5,0.5>
  uv.x *= iResolution.x/iResolution.y; // fix aspect ratio

  vec2 offset = vec2(0.0, 0.0);
  float f = sdfSquare(uv, 0.1, offset);

  vec3 col = (f*vec3(1.0,0.7,0.2))+((1.0-f)*texture(iChannel0, originalUV)).rgb;



  // Output to screen
  fragColor = vec4(5.0*col,1.0);
}
