void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = vec2(.5,.5);
    vec2 offset = (uv - pos);
    vec2 rpos = offset * vec2(iResolution.x / iResolution.y, 1);
    float dis = length(rpos);
    float alpha;
    float range = 0.6;
    if (dis<=range)
    {
        alpha = 1.;
    }
    else if (dis<range+0.5*range)
    {
        alpha = -2. * dis / range + 3.;
    }              
    vec3 col = texture(iChannel0,uv).rgb;
    vec3 col2 = vec3((0.2) * col.r + (0.7) * col.g + (0.1) * col.b);
    fragColor = vec4(mix(col,col2,1.25 - alpha) * (alpha),1.0);
}
