void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float t = iTime * 3. + 1.;

    // Seesaw: https://www.desmos.com/calculator/fw4axmvo35
    vec3 col = vec3( sin(t) * (uv.x - 0.5) + 0.5 );





     // Time varying pixel color
    vec3 rainbow = 0.5 + 0.5*cos(iTime+uv.xxx+vec3(0,2,4));
    col = col * rainbow;

    // Output to screen
    fragColor = vec4(col,1.0);
}
