void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Coords
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    uv.x *= ar; // make coords square

    float t = 40.*(.5-.5*cos(.3*iTime));

    // Shaping
    vec2 st = uv-vec2(.5*ar, .5);
    st *= .05*t;
    float r = 5.*length(st);
    float phi = atan(st.y/st.x);

    vec3 f = vec3(0.);
    f.r = .5*cos(8.*r + .5*cos(50.*phi)+cos(r*t*phi/3.)) + .5;
    f.g = .5*cos(5.*r + .5*cos(50.*phi)+cos(r*t)) + .5;
    f.b = .5*cos(8.*r + 1.*cos(50.*phi+9.)+cos(phi*t)) + .5;

    f.r = 0.;
    //f.g = 0.;

    f = smoothstep(0., 1., f);

    // Pixel Color
    vec3 col = f; // + 0.5*+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.);
}
