float maxIter = 40.0;
float expo = 2.0;
float limit= 2.0;

float mBrot(vec2 pos){
    vec2 r = vec2(0.0,0.0);

    limit = sin(iTime*0.3)+limit;

    for(float i=0.0; i < maxIter ; i++){
        r = vec2(pow(r.x,expo)-pow(r.y,expo),
                expo*r.x*r.y) + pos;

        if(length(r) > limit) return i/maxIter;
    }

    return maxIter;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xx;

    float mask = mBrot(3.8*uv-vec2(2.5,1.1));

    vec3 col = vec3(mask);

    // Output to screen
    fragColor = vec4(col,1.0);
}
