void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float wiggle = sin(iTime*5.0+ (uv.y*10.0))*0.01;

    float velx=1.5;
    float freqx=1.0;

    float vely=1.5;
    float freqy=1.0;

    float timeShiftx=(iTime*velx)+(uv.x*freqx);
    float timeShifty=(iTime*vely)+(uv.y*freqy);


    float shadow=0.5+cos(timeShiftx)+sin(timeShifty)*0.5;
    //return shadow;

    // Time varying pixel color

    float x= length(vec2 (uv.x*1.5-0.75,uv.y-0.5));


    // Output to screen
    if (x<0.3+wiggle)
    fragColor = vec4(1.0,0,0,1.0)*shadow;
    if(x>0.3+wiggle)
    fragColor = vec4(1,1,1,1)*shadow;
}
