void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = 1. - uv.x;

    vec3 cam = texture(iChannel0, uv).rgb;

    // color invert with consistant brightness
    vec3 col = normalize(vec3(1.01 - cam.rgb)) * (length(cam.rgb + vec3(0.01)) / 1.73) ;
    // I dont understand luminosity but im guessing thats why this looks so much darker than natural lighting

    // Output to screen
    fragColor = vec4(col,1.0);
}
