vec2 Position = vec2(0.0,0.0);
float Radius = 0.25;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aa = 2./ iResolution.y;
    float width = .5  + aa;

	vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    // vec4 gradientColor = 1.0-vec4(length(uv/width)+(Radius/width));
	vec4 gradientColor = vec4(1.0 - length(uv/width*2.0)+(Radius*2.0/width));


	fragColor = gradientColor;

}
