#define PI 3.14159265359 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 nfragCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
    nfragCoord.x *= (iResolution.x / iResolution.y);

    vec3 color = vec3(0.0);

    for (float i = 0.0; i < 2.0 * PI; i += 0.1)
    {

        color.r += vec3(0.004 / length(nfragCoord - vec2(cos(i / PI + iTime), sin(i + iTime + PI) / 5.0))).x;
        color.g += vec3(0.004 / length(nfragCoord - vec2(cos(i / PI + iTime), sin(i + iTime + PI * (1.0 / 2.0)) / 7.0))).y;
        color.b += vec3(0.004 / length(nfragCoord - vec2(cos(i / PI + iTime), sin(i + iTime) / 11.0))).x;

    }



    fragColor = vec4(vec3(color), 1.0);
}
