void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    const float gridSize     = 4096.0;
    const float cellSize     = 1.0 / gridSize;
    const float halfCellSize = cellSize / 2.0;

    // Calculate the row and column index of the current cell
    float row = floor((uv.y - halfCellSize) / cellSize);
    float col = floor((uv.x - halfCellSize) / cellSize);

    // Calculate the RGB values for the current cell
    //float r = uv.x;
    float r = (mod(col, 17.0) + mod(row, 16.0) * 16.0) / 255.0;
    float g = col / gridSize;
    float b = 1.0 - row / gridSize;

    // Set the pixel color to the RGB values
    fragColor = vec4(r, g, b, 1.0);
}
