#define eps .00001
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define PI acos(-1.)
const float DEG2RAD = PI/180.;

float sdCircle(vec2 p, float r)
{return length(p)-r;}

float sdCapsule(vec2 p, vec2 a, vec2 b, float r )
{
    vec2 pa=p-a, ba=b-a;
    float h=clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    return length(pa-ba*h)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 res=iResolution;
    vec2 p=(2.*fragCoord.xy-res.xy)/res.y;
    vec3 col=vec3(0,.67,.93);
    vec2 mp=normalize((2.*iMouse.xy-res.xy)/res.y);
    mp*=.15;

    float face=sdCircle(p,.9);
    float eye=min(sdCircle(vec2(p.x-.35,p.y-.25),.28),
                  sdCircle(vec2(p.x+.35,p.y-.25),.28));
    float eye2=min(sdCircle(vec2(p.x-.35,p.y-.25)-mp,.13),
                   sdCircle(vec2(p.x+.35,p.y-.25)-mp,.13));
    p.x+=.1;
    p.xy*=rot(65.*DEG2RAD);
    vec2 q=p;
    q.yx*=rot(pow(abs(p.y), 0.2));
    q.y+=.35;
    float mouth=sdCapsule(q,vec2(.325,0),vec2(-.325,0),.055);

    if(face<eps) col=vec3(1,.8,.3);
    if(eye<eps) col=vec3(1);
    if(eye2<eps) col=vec3(.4,.27,0);
    if(mouth<eps) col=vec3(.4,.27,0);
    fragColor = vec4(col,1.0);
}
