#define MAX_ITER 100

const float halfPI = 1.57079632679;

float atan2(vec2 xy){
    if(xy.x>0.) return atan(xy.y/xy.x);
    if(xy.y>0.) return halfPI-atan(xy.x/xy.y);
    if(xy.y<0.) return -halfPI-atan(xy.x/xy.y);
    if(xy.x<0.) return atan(xy.y/xy.x) + 2.*halfPI;
}

vec2 cis(float t){
    return vec2(cos(t), sin(t));
}

vec2 toPolar(vec2 ab){
    return vec2(length(ab), atan2(ab));
}
vec2 toCartesian(vec2 rt){
    return cis(rt[1]) * rt[0];
}

vec2 complexPow(vec2 rt, vec2 ab){
    //polar
    return vec2(exp(ab[0]*log(rt[0])-rt[1]*ab[1]), ab[1]*log(rt[0])+rt[1]*ab[0]);
}

int mandelbrot(vec2 c){
    vec2 z = c;
    int i = 0;
    vec2 m = iMouse.xy;
    if(m == vec2(0)) m = (vec2(sin(iTime), cos(iTime/2.+1.9))+2.)*100.;
    vec2 two = vec2(2,0) + (m/iResolution.xy*2.-1.);
    while(abs(z.x*z.x + z.y*z.y) <= 4. && i < MAX_ITER){
        z = toCartesian(complexPow(toPolar(z), two)) + c;
        i++;
    }
    return i;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates
    //x [-2; 1]
    //y [-1; 1]
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = uv.x*3. - 2.;
    uv.y = uv.y*2. - 1.;
    // Output to screen
    float col = 1.-float(mandelbrot(uv))/float(MAX_ITER);
    fragColor.rgb = vec3(col);
}
