void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord/iResolution.xy;
vec2 st = uv-.5;
float d = length(st);

vec3 col = vec3(0.01);

vec2 offs = st*.008*d;
col.r += texture(iChannel0, uv-offs).r;
col.g += texture(iChannel0, uv).r;
col.b += texture(iChannel0, uv+offs).r;

float rotation = iTime * 5.5;
vec2 rotateCoord = mat2(cos(rotation), -sin(rotation), sin(rotation), cos(rotation)) * uv;
float pattern = sin(rotateCoord.x * 1000.0) * cos(rotateCoord.y * 1000.0);

col += vec3(pattern);

vec2 f = uv;
f = .5 + 20.5*sin(iTime + f.yx);
col += vec3(fract(f.x * f.y));

fragColor = vec4(col,5.0);
}
