// Damn your eyes
// @P_Malin

// Epilepsy warning or something - Click image to animate.

// Not sure how I arrived at this but I guess I saw something similar somewhere 
// probably a Vasarely painting as Fabrice suggests.

void mainImage( out vec4 c, vec2 p )
{
vec2 uv = (p.xy / iResolution.xy) * 2. - 1.;
uv.x *= iResolution.x / iResolution.y;
float r = max( 0., 1. - length(uv) );
float t = iTime * 2.0;
if (iMouse.x <= 0.) t = 1.;
t = r * r * sin(r+t) * 3.0;
uv *= mat2( cos(t), -sin(t), sin(t), cos(t) );

// Add more colors to the final color by using the sin function
vec3 color  = ( sin( uv.x * vec3(1,2,3)*150. ) * .5 + .5 )
              * cos(iTime * vec3(1,1.5,2)*.1 );
color.r = sin( uv.x * 150. ) * 0.5 + 0.5;
color.g = sin( uv.x * 300. ) * 0.5 + 0.5;
color.b = sin( uv.x * 450. ) * 0.5 + 0.5;

// Use the cos function to make the colors more vibrant
color.r *= cos(iTime * 0.1);
color.g *= cos(iTime * 0.15);
color.b *= cos(iTime * 0.2);

c = vec4( color, 1.0 );
}
