float noise(vec2 coord)
{
    return fract(sin(dot(coord, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = vec3(0.0);

    // TV noise color
    color.r = 0.5 + 0.5 * noise(uv * 10.0 + vec2(iTime, 0.0));
    color.g = 0.5 + 0.5 * noise(uv * 10.0 + vec2(0.0, iTime));
    color.b = 0.5 + 0.5 * noise(uv * 10.0 + vec2(iTime, iTime));

    // Flickering effect
    color *= 0.5 + 0.5 * noise(uv * 30.0 + vec2(iTime, iTime));

    fragColor = vec4(color, 1.0);
}
