//Taken from iq
float sdCircle( in vec2 position, in float radius ) 
{
    return length(position)-radius;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 position = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float innerCircle = 0.5;
    float animateRadius = smoothstep(1.0,  0.2, iTime) * (2.0 - innerCircle) + innerCircle;    
	float dist = sdCircle(position,animateRadius);
    float rings = 5.7;
    float outerBrightness = sdCircle(position, animateRadius);
    outerBrightness /= 2.0;
    outerBrightness = 1.0 - outerBrightness;
    outerBrightness = clamp(outerBrightness, 0.8, 1.0);

    //main colors
    vec3 color = (dist > 0.0) ? vec3(0.690,0.239,0.082) : vec3(0.000,0.169,0.333);
    //shading
    color = (dist > 0.0) ? (color * (-dist/4.0+1.2) + 0.4*-fract((-dist*1.5+rings)*dist)) * outerBrightness : color * (-dist+0.2);

	fragColor = vec4(color,1.0);
}
