void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*1000.;

    vec3 col = vec3(sin(uv.y) * 2. * cos(uv.y+iTime) *4.*(sin(uv.x-iTime)));
    //location
    vec3 colors = vec3(0.2,0.5,0.1);
    float shift = -200.0;
    col *=  uv.x * colors -100. * 0.2*sin(iTime)+ shift;
    col -= (uv.y);


    // Output to screen
    fragColor = vec4(col,1.0);
}
