void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float sharpen = 0.8;

    float around = sharpen * -1.0;
    float center = sharpen * 4.0 + 1.0;

    vec2 uv = fragCoord/iResolution.xy;

    vec2 shift = 1.0 / iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb * center;

    col += texture(iChannel0, uv + vec2(shift.x,0.0)).rgb * around +
           texture(iChannel0, uv + vec2(0.0,shift.y)).rgb * around +
           texture(iChannel0, uv + vec2(-shift.x,0.0)).rgb * around +
           texture(iChannel0, uv + vec2(0.0,-shift.y)).rgb * around;

    // Output to screen
    fragColor = vec4(col,1.0);

}
