void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;

    vec2 uv = vec2((fragCoord.x/iResolution.x), (fragCoord.y/iResolution.y)/aspect);
    uv = fragCoord - iResolution.xy*0.5;
    uv /= iResolution.y;

    float r = 0.05;
    float time = iTime / 0.5;
    float px = cos(time) * 0.25;
    float py = sin(time) * 0.25;
    vec3 ccol = vec3((sin(time) + 1.) / 2., (cos(time) + 1.) / 2., sin(time) * cos(time) + (1./2.));
    vec3 bcol = vec3(1) - ccol;

    vec3 col = vec3(bcol);

    if(uv.x > px - r && uv.x < px + r){
        if(uv.y > -sqrt(pow(r, 2.) - pow(uv.x - px, 2.)) + py && uv.y < sqrt(pow(r, 2.) - pow(uv.x - px, 2.)) + py){
            col = vec3(ccol);
        }
    }

    fragColor = vec4(col,1.0);
}
