renderpass.code:
float rand(vec2 co){
    return texture(iChannel0, co / 10.0).r;
}

vec2 rPoint(vec2 co) {
    float x = rand(co);
    float y = rand(co + x);
    vec2 p = vec2(x, y) * 0.5 + 0.25;
    return p;
}

float sdfCircle(vec2 p, vec2 c, float r) {
    return length(c - p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    uv *= 10.0;
    uv += iTime;

    vec2 i = floor(uv);
    vec2 f = fract(uv);

    vec2 c = rPoint(i);

    float freq = 3.14 * rand(i + 6.1234);
    float r = sin(iTime * freq + rand(c + i)) * 2.0;
    r *= 0.05;
    float d = sdfCircle(f, c, r);

    d = smoothstep(0.05, 0.1, d);

    vec3 col = vec3(
        rand(i),
        rand(i + c),
        rand(i + 234.3)
    );

    col = mix(vec3(1.0), col, 1.0 - d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
