vec2 pivot = vec2(0.5, 0.5);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float time = mod(float(iFrame), 500.0);
    float A = (1.0 - smoothstep(100.0, 300.0, time)) * 0.01;
    vec2 dir = pivot - uv;
    float dis = length(dir);
    float f = A * sin(dis * 300.0 - iTime * 30.0) * (1.0 - smoothstep(0.1, 0.4, dis));
    vec2 offset = normalize(pivot) * f;

    vec3 col = texture(iChannel0, uv + offset).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
