#define R iResolution

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / R.xy;
    vec2 uvs = (fragCoord - 0.5 * R.xy ) / R.y;

    int tx = int(uv.x*512.0);

	float fft  = texelFetch( iChannel0, ivec2(tx,0.), 0 ).x; 

    float wave = texelFetch( iChannel0, ivec2(tx,0.4), 0 ).x;

       uv.x += (iTime / 10. ) ;
       uv.y += sin(uv.x * 10.) / 10. * (cos( wave ) / sin( wave ));
       float line = smoothstep(0.5, 0.5, uv.y) - smoothstep(0.51, 0.52, uv.y);
       line += smoothstep(0.6, 0.6, uv.y) - smoothstep(0.61, 0.3, uv.y);
       float circle = smoothstep(0., 0.4, length(uvs + vec2(fft)));
       line += circle;
    // Time varying pixel color

    vec3 col = vec3( 0., 0., 0.6 );

    // Output to screen
    fragColor = vec4(line * col,1.0);
}
