float hash(vec2 p)
{
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

vec3 getBrush(
vec2 uv, float xOffset, float yOffset, float xTurn, float yTurn, float intensity
)
{
    vec2 gv = vec2(
        uv.x + xOffset + hash(uv) * xTurn,
        uv.y + yOffset + hash(uv) * yTurn
    );
    return vec3(intensity / length(gv));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);
    col += getBrush(uv, 0.9, -0.2, -1.0, 1.0, 0.01) * vec3(1.0, 0.729, 0.471);
    col += getBrush(uv, -0.5, -0.6, 1.8, 0.8, 0.04) * vec3(1.0, 0.471, 0.471) * hash(uv);
    col += getBrush(uv, -0.2, 0.2, 0.0, 0.0, 0.05) * hash(uv) * 0.8;

    fragColor = vec4(col, 1.0);
}
