float pixellize(float x) {
    float pixel = 0.15;    
    return floor(x * pixel)/pixel;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );

	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame)*nyanWidthUV;



    float yOff = 12. * sin(0.01 * fragCoord.x + frame * 1.f); 

    vec3 col = vec3(0.);

    float minY = iResolution.y * 0.4 + yOff; 
    minY = pixellize(minY);
    float maxY = iResolution.y * 0.62 + yOff;
    maxY = pixellize(maxY);
    float maxX = iResolution.x * 0.78; 

    float y = pixellize(fragCoord.y);

    if(fragCoord.y < maxY && fragCoord.y > minY && fragCoord.x < maxX) {

       float thickness = 20.;
       if(y < thickness + minY) {
           col = vec3(0.4, 0, 1.0);
       } else if (y < 2. * thickness + minY) {
           col = vec3(0., 0.5, 0.8);
       } else if (y < 3. * thickness + minY) {
           col = vec3(0., 0.8, 0.2);
       } else if (y < 4. * thickness + minY) {
           col = vec3(255., 240., 0.)/255.;
       } else if (y < 5. * thickness + minY) {
           col = vec3(235., 149., 50.)/255.;
       } else if (y >= 5. * thickness + minY) {
           col = vec3(1., 0., 0.);
       } 

    } else {
        col = vec3(40., 70., 117.)/255.;
    }



	vec4 nyan = texture( iChannel0, uv );
	if(nyan.a != 0.f) {
        col = nyan.xyz;
    }



    fragColor = vec4( col, 1.0 );
}
