void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord /iResolution.xy;

    //Adjust the amount of bloom here
    float bloomAmount = 0.5;

    vec4 color = texture(iChannel0, uv) * bloomAmount + texture(iChannel1, uv);

    fragColor = color;


}

float sRGBtoLin(float colorChannel)
{
    //This converts a gamma encoded RGB to a linear value.

    if(colorChannel <= 0.04045)
    {
        return colorChannel / 12.92;
    }
    else
    {
        return pow(((colorChannel + 0.055) / 1.055), 2.4);
    }
}

float lumToPerLight(float luminance)
{
    //This transforms the luminance value to the perceptual lightness as seen by humans
    if(luminance <= 216.0/24389.0)
    {
        return luminance * (24389.0/27.0);
    }
    else
    {
        return pow(luminance, (1.0 / 3.0)) * 116.0 - 16.0;
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = texture(iChannel0, uv);

    //This calculates the luminance
    float lum = (0.2126 * sRGBtoLin(color.r) + 0.7152 * sRGBtoLin(color.g) + 0.0722 * sRGBtoLin(color.b));
    float perLight = lumToPerLight(lum) / 100.0;


    //Adjust the lightness threshold here
    if(perLight >= 0.85)
    {
    fragColor = vec4(perLight, perLight, perLight, 1.0);
    }
    else
    {
    fragColor = vec4(0.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float kernelSize = 9.0;
    //the -1.0 only if it's an odd number I think idk haven't tested
    float halfKernelSize = (kernelSize-1.0) / 2.0;

    float[] gaussian = float[] (1.0, 8.0, 28.0, 56.0, 70.0, 56.0, 28.0, 8.0, 1.0);
    //sum of all the values in the array
    float sum = 256.0;

    vec4 color = vec4(0.0, 0.0, 0.0, 1.0); 
    vec2 currentCoordinate = vec2(0.0, 0.0);

    for(float i = -halfKernelSize; i <= halfKernelSize; i++)
    {
        currentCoordinate = vec2(fragCoord.x + i, fragCoord.y);
        color.r += texture(iChannel0, currentCoordinate / iResolution.xy).r * gaussian[int(i+halfKernelSize)];
        color.g += texture(iChannel0, currentCoordinate / iResolution.xy).g * gaussian[int(i+halfKernelSize)];
        color.b += texture(iChannel0, currentCoordinate / iResolution.xy).b * gaussian[int(i+halfKernelSize)];
    }

    color = vec4(color.r / sum, color.g / sum, color.b / sum, 1.0);
    fragColor = color;
}

