Main {
    Q = vec4(1.,.7,1.1,1)*A(U)/float(iFrame);

    Q *= 1.+6.*fwidth(length(Q));
    Q = pow(max(Q,0.),vec4(1.25));
    if (length(Q.xyz)>0.) 
    Q.xyz = normalize(Q.xyz) * .8*atan(2.4*length(Q)); else Q = vec4(0);
}

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define A(U) texture(iChannel0,(U)/R)
vec4 mul (vec4 a, vec4 b) {

    // (a + bi + cj + dk) * (e + f*i + g*j + h*k)

    return vec4 (

        a.x*b.x - a.y*b.y - a.z*b.z - a.w*b.w  ,

        a.x*b.y + b.x*a.y + a.z*b.w - a.w*b.z  ,

        a.x*b.w + b.x*a.z + a.w*b.y - b.w*a.y  ,

        a.x*b.z + b.x*a.w + a.y*b.z - b.y*a.z

    ); 


}
vec4 fractal (vec4 u) {
    vec4 c = vec4(0);
    vec4 q = u;
    for (float i = 0.; i < 120.; i++) {
        u = mul(u,u) - vec4(.8,.15,0,.0);
        if (length(u) < 20.) 
            c += 2.*max(sin(sqrt(1.+i*10.)+vec4(1,2,3,4)),0.)*exp(-300.*dot(u,u));
        else break;
    }
    return 7.*(c);

}

Main {
    Q = A(U);
    U = 2.*(U+vec2(sin(422.*iTime),sin(1453.*iTime))-.5*R)/R.y;
    vec3 p = vec3(0,0,-2.8);
    vec3 d = normalize(vec3(U,3));
    float t = .3;
    p.x -= .1;
    p.xy *= mat2(cos(t),sin(t),-sin(t),cos(t));
    d.xy *= mat2(cos(t),sin(t),-sin(t),cos(t));
    t = .2;
    p.xz *= mat2(cos(t),sin(t),-sin(t),cos(t));
    d.xz *= mat2(cos(t),sin(t),-sin(t),cos(t));

    p += d*(2.9+.7*(fract(.5+float(iFrame)/500.))*2.-1.);
    Q += fractal(vec4(p,0));
}
