void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // limit rendering to the lower left of the screen
    const float size = 258.0;    
    if (fragCoord.x > size || fragCoord.y > size)
    {
        fragColor = vec4(0.0);
        return;
    }

    const float PI = 3.1415926535897932384626433832795;
    // calculate uv
	vec2 uv = fragCoord/size;
    vec2 uv_hat = uv - vec2(0.5f);
    vec2 x_axis = vec2(1.f, 0.f);
    float theta = acos(dot(normalize(uv_hat), x_axis));
    if (uv_hat.y < 0.f) theta = 2.f * PI - theta;
    float x = length(uv_hat) * cos(theta + iTime)/2.f + 0.5f;
    float y = length(uv_hat) * sin(theta + iTime)/2.f + 0.5f;
    vec2 new_uv = vec2(x, y); 
    new_uv = clamp(new_uv, vec2(0.f, 0.f), vec2(1.f, 1.f));

    // sample texture
    fragColor = texture(iChannel0, new_uv);
}
