void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 col = texture(iChannel0, fragCoord.xy / iResolution.xy);
    fragColor = col * col.a;
}

#define STEP 4.

float lookup(vec2 p) {
    vec4 c = texture(iChannel1, p);
	return dot(c, c);
}

float sobel(vec2 p) {
    vec3 offset = vec3(STEP / iResolution.x, 0, STEP / iResolution.y); 
    float tl = lookup(p + vec2(-1,  1) * offset.xz);
    float cl = lookup(p + vec2(-1,  0) * offset.xy);
    float bl = lookup(p + vec2(-1, -1) * offset.xz);
    float ct = lookup(p + vec2( 0,  1) * offset.yz);
    float cb = lookup(p + vec2( 0, -1) * offset.yz);
    float tr = lookup(p + vec2( 1,  1) * offset.xz);
    float cr = lookup(p + vec2( 1,  0) * offset.xy);
    float br = lookup(p + vec2( 1, -1) * offset.xz);
    vec2 g = vec2(tl + 2. * cl + bl - tr - 2. * cr - br,
                 -tl - 2. * ct - tr + bl + 2. * cb + br);
    return dot(g, g);
 }

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float s = texture(iChannel2, vec2(0)).r;
    vec2 uv = (1. - s * 0.015) * (fragCoord.xy / iResolution.xy - 0.5) + 0.5;
    vec3 col = vec3(0.8, 0.2, 0.1) * sobel(uv); 

    uv += sin(30. * length(uv - 0.5) + iTime * 5.) * (uv.yx - 0.5) * 0.01 / (15. * length(uv - 0.5) + 1.);
    vec3 fire = vec3(mix(texture(iChannel0, uv).rgb, col, 0.05));
    fragColor = vec4(fire, s);
}
