vec3 wave(vec2 uv, vec2 pos, float offset, float len, vec3 color) {
    uv -= pos;

    float d = length(uv * iResolution.xy);
    vec3 outColor = sin(d / len - offset) * color;

    return outColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float vel = 3.0;
    vec3 waveColor = normalize(vec3(0, 133, 255));

    vec3 col = wave(uv, vec2(0, 0), iTime*vel, 10.0, waveColor);
    col += wave(uv, vec2(1, 0), iTime*vel, 10.0, waveColor);
    col += wave(uv, vec2(0, 1), iTime*vel, 10.0, waveColor);
    col += wave(uv, vec2(1, 1), iTime*vel, 10.0, waveColor);

    fragColor = vec4(col,1.0);
}
