void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = gl_FragCoord.xy / iResolution.xy;

  uv += iTime * .1;

  // calculate the color of the pixel
  vec3 color = vec3(0.0);

  float african1 = smoothstep(0.1, 0.15, sin(uv.x * 20.0 + uv.y * 20.0));
  color += vec3(0.8, 0.6, 0.2) * african1;

  float african2 = smoothstep(0.3, 0.35, sin(uv.x * 10.0 + uv.y * 10.0));
  color += vec3(0.4, 0.2, 0.0) * african2;

  float african3 = smoothstep(0.5, 0.55, sin(uv.x * 5.0 + uv.y * 5.0));
  color += vec3(0.2, 0.1, 0.0) * african3;

  // gl_FragColor = vec4(color, 1.0);


  // output the color of the pixel
  fragColor = vec4(color, 1.0);
}
