float metaballSize = 0.42; // Size of the metaballs
float speed = 0.3; // Speed of movement for the floating metaballs
int numBalls = 12; // Number of floating metaballs

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec2 balls[5];

    float d = 0.0;

    for (int i = 0; i < numBalls; i++) {
        float index = float(i);
        float x = sin(index + 1.0 * 0.2 + iTime * 0.1599 * speed + index) * 0.9;
        float y = cos(index + 1.0 * 0.189 + iTime * 0.8878 * speed) * 0.5;
        balls[i] = vec2(x, y);
        d += metaballSize / length(uv - balls[i]);
    }

    // Adds a metaball that follows the mouse
    vec2 mousePos = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;
    balls[numBalls] = mousePos;
    d += metaballSize / length(uv - balls[numBalls]);

    d = smoothstep(0.78, 0.8, d * 0.07);

    fragColor = vec4(d);
}
