vec3 colors[5] = vec3[5](
    vec3(0.639, 0.080, 0.384), // LOVE
    vec3(0.827, 0.384, 0.643), // FREEDOM
    vec3(1.000, 1.000, 1.000), // GENDER NONCONFORMITY
    vec3(1.000, 0.604, 0.337), // COMMUNITY
    vec3(0.835, 0.176, 0.000)  // TRANSGRESSIVE WOMANHOOD
);

float dot2( in vec2 v ) { return dot(v,v); }

//https://www.shadertoy.com/view/3tyBzV
float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    float distHeart = sdHeart(p+vec2(0.0,0.5))-iTime*0.33;
    int stripe = int(fract(distHeart)*5.0);

    vec3 col = colors[stripe];

    // Output to screen
    fragColor = vec4(col,1.0);
}
