vec3 palette(float t){

    vec3 a = vec3(0.748, 0.638, 0.602);
    vec3 b = vec3(-0.194, 0.48, 1.168);
    vec3 c = vec3(-2.142, -0.972, 1.000);
    vec3 d = vec3(-0.552, -1.002, 0.667);
    return a + b * cos(6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy)/ iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = .0 ; i < 3.; i++){
        uv = fract(uv*2.)-.5;

        float d = length(uv) * exp(-length(uv0)*.6);

        vec3 col = palette(length(uv0) + i*.4 + iTime*.4);

        d = sin(d*8. - 1.*iTime)/8.;
        d = abs(d);

        d = pow(0.01/d, 1.1);

        finalColor += col*d;
    }

    fragColor = vec4(finalColor, 1.);
    }
