#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1.));  
    for(float i=0.,g=0.,e,s;
        ++i<99.;
        O+=sin(vec4(22.*(1.+0.95*sin(iTime*0.12)),15,53,1)/s/e)/e/1e4
    )
    {
        p=g*d;
        p.z-=3.;
        p=R(p,normalize(vec3(1,5,0)),iTime*.15);  // iTime*0.15
        s=3.;
        for(int i=0;i++<6;)               // de i++<6
            p.xyz=0.34-(abs(p.xyz)),    //p.xy=p.xyz
            p.x<p.z?p=p.zyx:p,
            s*=e=2.6/clamp(dot(p,p),.02,1.2),
            p=abs(p)*e-vec3(.1,15,2)*0.75;   // *0.75

      if( mod(iTime,40.)<20.)
         {  g+=e=length(p.xyz)/s+5e-4;  }    // p.xy=>p.xyz Shanpan glass
         else{
        g+=e=length(p.xy)/s+5e-4*2.5;}       // default  
         }
}
