/*
This is just to play around with my own solution

A cleaner solution can be found here
https://www.shadertoy.com/view/wt23Rt
*/

float HSVtoRGBHelperF(float n, vec3 hsv)
{
    float k = mod(n+hsv.x/60.0, 6.);
    return hsv.z-hsv.z*hsv.y*clamp(min(k,4.-k),0., 1.);
}

vec3 HSVtoRGB(vec3 hsv)
{
    vec3 rgb = vec3(HSVtoRGBHelperF(5., hsv), HSVtoRGBHelperF(3., hsv), HSVtoRGBHelperF(1., hsv));
    return rgb;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 hsv = vec3(length(uv)*360.*4.+iTime*60.,uv.x*2.,uv.y*2.);
    vec3 col = HSVtoRGB(hsv);

    // Output to screen
    fragColor = vec4(col,1.0);
}
